import React, { Component } from 'react'

export default class TodoInput extends Component {

    render() {
        const {title,handleChange,handleSubmit,EditTitle} = this.props;
        return (
            <div className="card card-body my-3">
                <form onSubmit={handleSubmit}>
                    <div className="input-group">
                        <div className="input-group-prepend">
                            <div className="input-group-text bg-primary text-white">
                                <i className="fa fa-wrench size "></i>

                            </div>
                        </div>
                        <input type="text" className="form-control" placeholder="Enter the Task "  value={title} onChange={handleChange} required/>
                    </div>
                    <button className={EditTitle ? "btn btn-block btn-success mt-3":"btn btn-block btn-primary mt-3"}>{EditTitle ? "Edit":"Add"}</button>
                </form>

            </div>
        )
    }
}
